INSERT INTO `article_in_press_tbl` (`article_id`, `cat_id`, `journal_id`, `year`, `article_name`, `article_url`, `article_type`, `author_name`, `article_month`, `published_date`, `short_desc`, `meta_title`, `meta_desc`, `meta_keywords`, `research_article`, `abstract`, `introduction`, `reff_data`, `figures`, `suggested_citation`, `tables`, `article_pdf`, `article_image`, `created_date`, `status`, `finalstatus`) VALUES
(51, 1, 32, '2020', 'Multisite Prospective Comparison of Multiplex Polymerase Chain Reaction Testing with Urine Culture for Diagnosis of Urinary Tract Infections in Symptomatic Patients', 'multisite-prospective-comparison-of-multiplex-polymerase-chain-reaction-testing-with-urine-culture-for-diagnosis-of-urinary-tract-infections-in-symptomatic-patients', 'Research Article', 'Annah Vollstedt , David Baunoch*, Kirk J. Wojno , Natalie Luke , Kevin Cline , Laurence Belkoff , Aaron Milbank , Neil Sherman , Rashel Haverkorn , Natalie Gaines , Neal Shore, Howard Korman , Xinhua ', 'July', '13/07/2020', ' Both M-PCR and SUC were performed on urine samples from 2511 patients (mean age 73; range 24 - 100) with UTI symptoms.', 'Multisite Prospective Comparison of Multiplex Polymerase Chain Reaction Testing with Urine Culture for Diagnosis of Urinary Tract Infections in Symptomatic Patients', ' Both M-PCR and SUC were performed on urine samples from 2511 patients (mean age 73; range 24 - 100) with UTI symptoms.', 'Bacteria, Culture, Diagnosis; PCR, Urinary Tract Infection', '<p class=\"authorsname target=\"><em><b>Annah Vollstedt<sup>1</sup>, David Baunoch<sup>2*</sup>, Kirk J. Wojno<sup>3</sup>,  Natalie Luke<sup>2</sup>, Kevin Cline<sup>4</sup>, Laurence Belkoff<sup>5</sup>, Aaron Milbank<sup>6</sup>, Neil Sherman<sup>7</sup>,  Rashel Haverkorn<sup>8</sup>, Natalie Gaines<sup>8</sup>, Neal Shore<sup>10</sup>, Howard Korman<sup>3</sup>, Xinhua Zhao<sup>11</sup>,  Shuguang Huang<sup>11</sup>, Mohammad Jafri<sup>3</sup>, Patrick Keating<sup>3</sup>, Bridget Makhlouf<sup>3</sup>,  Dylan Hazelton<sup>3</sup>, Stephany Hindo<sup>3</sup>, David Wenzler<sup>3</sup>, Mansour Sabry<sup>3</sup>, Meghan Campbel<sup>9,12</sup>,  Dakun Wang<sup>12</sup>, Larry Sirls<sup>3</sup> </b></em></p>\r\n<p class=\"universityname target=\"><em><sup>1</sup>Beaumont Hospital, 3601 W. Thirteen Mile Rd, Royal Oak, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>2</sup>Pathnostics, 17661 Cowan, Irvine, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>3</sup>Comprehensive Urology-A Division of Michigan Healthcare Professionals, Woodward Ave, Royal Oak, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>4</sup>Regional Urology, LLC, 255 Bert Kouns, Shreveport, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>5</sup>Urologic Consultants of SE PA, 1 Presidential Blvd, Suite 115, Bala Cynwyd, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>6</sup>Minnesota Urology, 11850 Blackfoot Street NW, Suite 470, Coon Rapids, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>7</sup>Premier Urology, 10 Parsonage Road, Suite 118, Edison, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>8</sup>Urology San Antonio, 7909 Fredericksburg Rd, Suite 150, San Antonio, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>9</sup>Advanced Urology of SO FL, LLC, 5350 W Atlantic Blvd, 102, Delray Beach, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>10</sup>Carolina Urologic Research Center, 823 82nd Parkway, Suite B, Myrtle Beach, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>11</sup>Stat4ward, 711 Parkview Dr., Gibsonia, USA </em></p>\r\n<p class=\"universityname target=\"><em><sup>12</sup>University of Kentucky College of Medicine, 800 Rose Street MN 150, Lexington, USA </em></p>\r\n<p class=\"corresauthor target=\"><b><sup>*</sup>Corresponding author: </b>David Baunoch, Pathnostics, 17661 Cowan, Irvine, CA 92614, USA.  Tel: +1 714-966-1221; Fax: 714-966-1231; Email: dbaunoch@pathnostics.com</p>\r\n<p class=\"Dates target=\"><b>Received Date:</b> 22 Jan, 2020 ; <b>Accepted Date:</b> 02 Jan, 2020 ; <b>Published Date:</b> 08 Jan, 2020</p>', '<h3 class=\"subtitle target=\">Abstract</h3>\r\n<p><b>Objectives: </b>This study was conducted to compare Multiplex-Polymerase Chain Reaction (M-PCR) with Standard Urine Culture (SUC) for detection and identification of bacteria in Urinary Tract Infection (UTI)-symptomatic patients.</p>\r\n<p><b>Methods: </b>Both M-PCR and SUC were performed on urine samples from 2511 patients (mean age 73; range 24 - 100) with UTI symptoms.</p>\r\n<p><b>Results: </b>M-PCR and SUC detected bacteria in 62.7% (1575/2511) and 43.7% (1098/2511) of cases, respectively. SUC detected 21 bacteria, 18 of which were also detected by M-PCR with higher detection rates, especially for the Gram-positive species. M-PCR detected 24 bacteria, among which SUC failed to detect 6, including five Gram-positive bacteria (A. schaalii, A. omnicolens, C. riegelii, M. genitalium, and M. hominis), and one Gram-negative bacterium, (<em>U. urealyticum</em>). A total of 590 patients (23.5%) were detected by M-PCR to have at least 1 of the 6 bacteria. A total of 861 polymicrobial infections were reported, with M-PCR reporting 834 (96.9%) and SUC reporting 168 (19.5%). Polymicrobial detections constituted 34.3% (861/2511) of the total patients, 53.0% of M-PCR positives (834/1575) but only 15.3% of SUC positives (167/1098). <em>A. schaalii</em>, not detected by SUC, was the most common bacterium [53.0% (442/834)] detected in polymicrobial infections by M-PCR.</p>\r\n<p><b>Conclusions: </b>This prospective multicenter study of over 2500 UTI-symptomatic patients demonstrated the greater ability of M-PCR to detect bacteria, especially Gram-positive bacteria and polymicrobial infection, over SUC. Use of M-PCR may improve the detection of pathogenic bacteria and consideration of antibiotic resistance and susceptibility may lead to more effective treatment for UTI.</p>\r\n<h3 class=\"subtitle target=\">Keywords</h3>\r\n<p>Bacteria; Culture; Diagnosis; PCR; Urinary Tract Infection</p>\r\n<h3 class=\"subtitle target=\">Abbreviations</h3>\r\n<p>AST: Antibiotic Susceptibility Testing; CFU/ ml: Colony Forming Units Per Millilitre; CAN: Colistin And Nalidixic Acid Agar; DNA: Deoxyribonucleic Acid; M-PCR: Multiplex Polymerase Chain Reaction; MAC: Macconkey Agar; mL: Millilitre; P-AST: Pooled Antibiotic Susceptibility Testing; PCR: Polymerase Chain Reaction; &micro;L: Microliter; SUC: Standard Urine Culture; UTI: Urinary Tract Infection</p>', '<h3 class=\"subtitle target=\">Introduction</h3>\r\n<p>Urinary Tract Infection (UTI) is the most common infection seen by primary care physicians [1, 2]. The lifetime incidence of UTI in women is 50% to 60%. Recurrent UTI is also common, occurring in 14% to 27% of UTI patients [1, 3, 4]. Initial treatment failure, resulting in recurrent UTI, may occur for various reasons: UTI may be complicated, as in pyelonephritis; patients may not comply with treatment; the diagnosis may be incorrect; and antibiotic resistance may not have been recognized [5, 6]. Accumulating evidence has established that Standard Urine Culture (SUC) may not detect clinically significant bacteria [7]. Reliance on urine culture lead us to believe that urine is sterile. On the other hand, application of molecular methods such as Polymerase Chain Reaction (PCR) have demonstrated that urine is not sterile, even in healthy people [7, 8]. The study described herein was conducted to compare the performance of Multiplex PCR-Based Molecular Testing (M-PCR) with that of SUC for diagnosis of UTI in symptomatic patients.</p>\r\n<h3 class=\"subtitle target=\">Methods</h3>\r\n<h3 class=\"subsubtitle target=\">Selection and Description of Participants</h3>\r\n<p>Study participants were patients who presented with symptoms of UTI at urology clinics. Patients with no symptoms of UTI were not included. Patients were evaluated by any of 75 physicians from 37 urology offices in seven states. A total of 2511 consecutive patients were enrolled between July 26, 2018 and February 27, 2019. All patients provided written informed consent per forms approved by the Western IRB (20181661). Inclusion criteria included patients &ge; 60 years of age presenting with symptoms of acute cystitis, complicated UTI, persistent UTI, recurrent UTIs, prostatitis, and pyelonephritis, and patients at any age presenting with a history and symptoms of interstitial cystitis.</p>\r\n<p>Exclusion criteria included prior participation in this study, antibiotics use for any reason other than UTI at the time of enrollment, presence of chronic (&ge; 10 days) indwelling catheters, and self-catheterization. Patients with urinary diversion were excluded from the study. Patients without documented specimen collection and stabilization time, or sufficient specimen volumes to perform both urine culture and the M-PCR test coupled with Pooled Antibiotic Sensitivity Testing (P-AST) were also excluded from the study. Results from the P-AST are discussed in a separate manuscript.</p>\r\n<h3 class=\"subsubtitle target=\">Urine Culture</h3>\r\n<p>Urine culture in the study was performed following SUC procedures. Urine samples were obtained from patients by either self-administered clean catch or catheterization. Samples were collected and transported to Pathnostics (Irvine, California) for testing by culture. For culture, urine was vortexed and a sterile plastic loop (1 &micro;L) was used to inoculate blood agar plates. A sterile plastic loop (1 &micro;L) was used also to inoculate Colistin and Nalidixic Acid Agar/Macconkey Agar (CNA/MAC) plates, one loop-full of urine on the CNA side of the plate and another full loop-full on the MAC side of the plate. All plates were incubated at 35&ordm; C in 5% CO2 for &ge;18 hours and then examined for evidence of growth. Per CLSI Performance Standards for Antimicrobial Susceptibility Testing (30<sup>th</sup> Edition), plates with &lt; 104 CFU/ml were reported as normal urogenital flora.</p>\r\n<p>For plates with growth (&ge; 10<sup>4</sup> CFU/ml), the quantity and morphology of each organism was recorded. The maximum readable colony count using the 1 &micro;L loop is &gt; 10<sup>5</sup> CFU/ml. Colony counts were performed on blood agar plates. Species identification and colony counts were performed on CNA/MAC plates. For plates with &le; 2 pathogens, species identification and colony counts were reported for each pathogen with &ge; 10<sup>4</sup> CFU/ml. If &ge; 3 pathogens were present, and one or two were predominant, species identification and colony counts were reported. If &ge; 3 pathogens were present without predominant species, a mixed morphotype was reported. Pathogen identification was confirmed with the VITEK 2 Compact System (bioMerieux, Durham, NC) in accordance with standard operating procedures.</p>\r\n<p>Briefly, a sterile swab was used to transfer morphologically similar colonies from positive blood agar plates to prepared polystyrene test tubes containing 3.0 mL of sterile saline. The sample was adjusted for density (equivalent to McFarland No. 0.50 to 0.63). The sample tube and an appropriate identification card were placed into the cassette and inserted into the VITEK 2 instrument. The identity of the bacteria was used to determine Gram status, and a GN card was used for Gram-negative bacteria, and a GP card was used for Gram-positive bacteria. A YST card was used for yeast. Pathogen identification was read from the VITEK 2 instrument.</p>\r\n<h3 class=\"subsubtitle target=\">DNA extraction and M-PCR analysis</h3>\r\n<p>DNA was extracted from urine samples with the KingFisher/ MagMAX Automated DNA Extraction instrument and the MagMAX DNA Multi-Sample Ultra Kit (ThermoFisher, Carlsbad, CA). Briefly, 400&micro;L of urine were transferred to wells in 96- well deep-well plates, sealed, and centrifuged to concentrate the samples; supernatant was removed. Enzyme Lysis Mix (220&micro;L/ well) was added and incubated for 20 min at 65&ordm;C. Proteinase K Mix was added (50&micro;L/well) and incubated for 30 min at 65&ordm;C. Lysis buffer (125 &micro;L/well) and DNA Binding Bead Mix (40 &micro;L/ well) were added, and the samples shaken for a minimum of 5 min. The 96-well plate was loaded into the KingFisher/MagMAX Automated DNA Extraction instrument, which was operated in accordance with standard operating procedures.</p>\r\n<p>DNA samples were analyzed with the Pathnostics Guidance<sup>&reg;</sup> UTI Test. The samples were mixed with universal PCR master mix and amplified with TaqMan technology on a Life Technologies 12K Flex Open Array System. DNA samples were spotted in duplicate on 112-format Open Array chips. Plasmids for each organism being tested for were used as positive controls. Candida tropicalis was used as an inhibition control. A data analysis tool developed by Pathnostics was used to sort data, assess the quality of data, summarize control sample data, identify positive assays, calculate concentrations, and generate draft reports. Probes and primers were used for the following 23 bacteria and 2 bacteria groups:</p>\r\n<p><b>Bacteria:</b> <em>Acinetobacter baumannii, Actinotignum schaalii, Aerococcus urinae, Alloscardovia omnicolens, Citrobacter freundii, Citrobacter koseri, Corynebacterium riegelii, Klebsiella aerogenes, Enterococcus faecalis, Escherichia coli, Klebsiella oxytoca, Klebsiella pneumoniae, Morganella morganii, Mycoplasma genitalium, Mycoplasma hominis, Pantoea agglomerans, Proteus mirabilis, Providencia stuartii, Pseudomonas aeruginosa, Serratia marcescens, Staphylococcus aureus, Streptococcus agalactiae, and Ureaplasma urealyticum</em>.</p>\r\n<p><b>Bacterial groups:</b> <em>Coagulase Negative Staphylococci Group (CoNS), including Staphylococcus epidermidis, Staphylococcus haemolyticus, Staphylococcus lugdunesis, and Staphylococcus saprophyticus</em>, and Viridans Group <em>Streptococci</em> (VGS), <em>including Streptococcus anginosus, Streptococcus oralis, and Streptococcus pasteuranus</em>.</p>\r\n<p>The quantities of each of the bacterial species were determined using the standard curve method: first, standard curves of each of the bacterial species were generated from testing replicates of dilution series of known concentrations of the bacterium; constants necessary for the quantitation of each of the bacterial species in unknown samples, such as slope and intercept, were established from each of the standard curves; then PCR Ct values of a target bacterial species from an unknown sample were compared to the standard curve, and the concentration of the target bacterial species (cells/mL) present in the samples was extrapolate and determined. Bacterium with quantity of &ge;10,000 cells/mL was defined as &ldquo;positive&rdquo; or &ldquo;detected&rdquo;, and bacteria with quantity &lt; 10,000 cells/mL was defined as &ldquo;negative&rdquo; or &ldquo;not detected&rdquo;.</p>\r\n<h3 class=\"subsubtitle target=\">Statistics</h3>\r\n<p>Descriptive statistics were used to describe the data in the study.</p>\r\n<h3 class=\"subtitle target=\">Results</h3>\r\n<h3 class=\"subsubtitle target=\">Patient Demographics and Clinical Information</h3>\r\n<p>Patient demographics and clinical information are given in Table 1. Of the 2511 participants, 1360 (54%) were females and 1151 (46%) were males. The median age in this study was 73 and the range was 24-100 years, with 99% of patients over the age of 60. All patients were presented with symptoms of UTI, such as dysuria, cloudy or strong-smelling urine, pain or pelvic discomfort, fever, and Lower Urinary Tract Symptoms (LUTS). Positive urine  analysis, defined as the presence of blood, leukocytes or nitrites, was present in 75.5% of the study population. Antibiotic usages in the last 3 weeks were seen in 16.9% of female and 5.7% of male patients (Table 1).</p>\r\n<h3 class=\"subsubtitle target=\">M-PCR identified more UTI-symptomatic patients with bacteria in their urine than SUC</h3>\r\n<p>Overall, M-PCR detected bacteria in 62.7% (1575) whereas SUC detected bacteria in 43.7% (1098) of the 2511 patients. SUC and M-PCR were both positive in 1018/2511 (40.5%) cases and were both negative in 856/2511 (34.1%) cases. Only 80 out of the 2511 (3.2%) cases were positive in SUC but negative in M-PCR. On the contrary, 557 out of the 2511 cases (22.2%) were positive in M-PCR but negative in SUC (Table 2). M-PCR detected more bacteria species, especially Gram-positive species, in  UTIsymptomatic patients than SUC. The M-PCR panel in this study tested for 23 bacteria plus two bacterial groups. It detected 24 different bacteria or groups in the 2511 patients in the study, and SUC detected 21.</p>\r\n<p>Among the 21 bacteria detected by SUC, 18 were also detected by M-PCR, with higher detection rates by M-PCR than  SUC for almost all of them, especially for the Gram-positive species. For example, <em>E. faecalis</em>, CoNS, VGS, and <em>A. urinae</em>, were detected in 200 (8%), 72 (2.9%), 49 (2%), and 21 (0.8%) in the 2511 patients by SUC, and were 253 (10.1%), 309 (12/3%), 439 (17.5%), and 460 (18.3%) by M-PCR (Figure 1). <em>E. coli</em> was the most detected bacterium by both SUC and M-PCR, with detection rates of 21.2% and 22.7%, respectively. The top bacterial species detected by SUC, defined as at least a 1% detection rate) included 8 bacterial species, where M-PCR detected 13 bacteria with at least a 1% detection rate, including all 8 that were detected by SUC (Figure 1).</p>\r\n<p>Among the 24 bacteria detected by M-PCR, SUC failed to detect 6 of them, including five Gram-positive bacteria (<em>A. schaalii, A. omnicolens, C. riegelii, M. genitalium, and M. hominis</em>), and one Gram-negative bacterium, <em>U. urealyticum</em>. Four of the 6 were on the top list detected by M-PCR (Figure 1). A total of 590 patients (23.5%) were detected by M-PCR to have at least 1 of the 6 bacteria species in their urine samples. There were 3 bacterial species detected by SUC that were not detected by the M-PCR. They were the <em>Enterobacter</em> species, the <em>Enterococcus</em> species, and the other species, detected in only 0.9%, 0.2% and 0.9%, of all patients, respectively. This is not due to the failure of the M-PCR methods, but rather that the M-PCR panel did not include primers and probes for the three bacteria (Figure 1). Bacteria detected by SUC or M-PCR in male and female patients were detailed in (Supplementary Table 1).</p>\r\n<h3 class=\"subsubtitle target=\">M-PCR detected more polymicrobial infections in UTIsymptomatic patients than SUC</h3>\r\n<p>In this study, SUC was found to have limited capacity to detect polymicrobial infections compared with M-PCR (Table 3). The specific bacteria identified in polymicrobial infections are listed in Figure 2. Eight hundred sixty-one polymicrobial infections were detected by M-PCR and SUC combined. Among these detections, M-PCR detected 834 of 860 (96.9%), SUC detected 168 of 860 (19.5%) and 141 of 860 (16.4%) were polymicrobial by both M-PCR and SUC. Polymicrobial infections were detected in 34.3% (861/2511) of patients, 53.0% of M-PCR positives (834/1575) but only 15.3% of SUC positives (167/1098) (Table 3).</p>\r\n<p>One of the six bacteria that were detected by M-PCR but not by SUC, <em>A. schaalii</em>, was the most common bacterium involved in polymicrobial infections and was involved in 53.0% (442/834) of all polymicrobial detections by M-PCR. The total cases detected with <em>E. coli</em> were similar between SUC and M-PCR. However, SUC reported that <em>E. coli</em> was most commonly a monomicrobial infection where M-PCR reported <em>E. coli</em> most commonly as a polymicrobial infection. Similar results were observed in other bacteria species, such as <em>E. faecalis, K. pneumoniae</em>, and CoNS (Figure 2) (Supplementary Table 2).</p>\r\n<h3 class=\"subtitle target=\">Discussion</h3>\r\n<p>This prospective study of over 2500 patients with symptoms of UTI  found that M-PCR was positive for bacteria in 62.7%  of patients versus 43.7% with SUC. Twenty-two percent of PCR-positive  patients had a negative urine culture and PCR detected  five times more polymicrobial infections than did urine culture (834 vs. 167). These results are consistent with previous studies that have also shown that PCR provides advantages in detection sensitivity and identification specificity for the diagnosis of UTI [9-11]. PCR is expanding our understanding of the urobiome and alterations with infection and disease. SUC has a limited ability of to grow fastidious organisms, especially Gram-positive bacteria. As a result, PCR reports more bacteria, more different bacteria and more bacterial combinations. Others have demonstrated the poor performance of SUC.</p>\r\n<p>Hilt showed that in symptomatic and asymptomatic patients 92% had negative SUC, yet 80% of these were positive when using expanded quantitative urine culture techniques to improve growth of fastidious organisms [8]. Khasriya showed that culture of the shed urothelial cells (where uropathogens adhere) resulted in large numbers or bacteria that were undetected on SUC [12]. Kline reported that Gram-positive bacteria including Aerococcus, <em>Actinobaculum</em> and <em>Corynebacterium</em>, have been overlooked as uropathogens because of the limitations of the SUC [13]. In this study, six bacteria detected by M-PCR were not detected by culture and five of them were Gram-positive. Three of these bacteria (<em>A. schaalii, A. omnicolens, and C. riegelii</em>) were among the 10 most frequently detected bacteria by M-PCR, combining for 22.3% of all bacterial detections.</p>\r\n<p>Even more, <em>A. schaalii</em> was found in polymicrobial infections more often than any other bacterium: it was involved in 53.0% (442/834) of all M-PCR detected polymicrobial infections. Five of the six have also been reported as pathogenic for UTI and associated with increased risk for sepsis and bacteremia, endocarditis, Fournier&rsquo;s gangrene, and abdominal abscess [14-16]. Of the others, <em>A. schaalii</em> and <em>A. omnicolens</em> are acknowledged as uropathogens [17,18]. <em>C. riegelii</em> is commonly regarded as a commensal but has also been shown to cause UTI [19,20]. In addition, both <em>M. hominis</em> and <em>U. urealyticum</em> have been documented as causing LUTS, and may be antibiotic resistant [21,22]. Therefore, one cannot say that the organisms identified by PCR are not pathogenic, instead, that we do not yet understand their role in the normal urobiome or in a urinary tract infection.</p>\r\n<p>Studies like these are among the first to investigate these patients with both SUC and PCR and further work is ongoing. The M-PCR method detected five times as many polymicrobial UTI&rsquo;s than traditional culture. SUC is inherently limited in its ability to detect and identify pathogens in polymicrobial infections [23, 24]. The proportion of the total number of patients with M-PCR detected polymicrobial infections in this study was 33.2%, which is consistent with previous reports [7, 8]. Importantly, among patients who tested positive for bacteria with M-PCR, there were more polymicrobial infections (53.0%, 834/1575) than monomicrobial infections (47.0%, 741/1575). The study of urine bacteria by PCR in the patient with UTI symptoms will help us identify the abnormalities of the urobiome.</p>\r\n<p>The large number of Gram-positive organisms in the urine  identified in this and other studies may indicate an important role in the normal urobiome and in the pathogenesis of polymicrobial UTI, perhaps a critical role in the development of consortia, non-random communities of microbes that interact synergistically in providing community members with growth and survival advantages. Failure of standard culture to detect and identify bacteria also raises the possibility of treatment failure due to unidentified antibiotic resistance. Several papers have reported that clinical antibiotic resistance, i.e., antibiotic response in the patient, was different from resistance predicted on the basis of results of laboratory tests [25, 26]. This difference could occur for a number of reasons, but failure to grow bacteria will lead to failures of sensitivity testing.</p>\r\n<p>Our data showed that the M-PCR/P-AST test takes an average of 29.7 hours (9 hours less than SUC) to provide physicians with urine pathogen and drug sensitivity results. This difference is increase to a median of 19 hours (34.5 hours and 53.7 hours, for M-PCR/P-AST and SUC, respectively) for patients for both positive pathogen identification and susceptibility results. The faster turnaround time also places M-PCR in an advantageous position, compared to SUC.</p>\r\n<p>The current study was not designed to test the clinical advantage of M-PCR over SUC. All bacteria on the PCR panel and all bacteria detected in SUC have previously been described as causing UTI [7,8]. However, all bacteria do not contribute equally to pathogenesis. This is especially true for polymicrobial infections. Some bacteria may be more pathogenic than others, and interactions among bacteria in polymicrobial infections have yet to be described.</p>\r\n<p>The identification of more and different bacteria in urine from patients with UTI by PCR does not prove the bacteria are the cause of the patients&rsquo; symptoms. Proof of this relationship will depend on documenting that eradication of the putative pathogens results in resolution or improvement of symptoms. The detection criterion of &ge;10,000 cells/mL for M-PCR was also noteworthy.</p>\r\n<p>The threshold of &ge;10,000 cells/mL was selected to be consistent with the current standard of care. Studies have shown that colony counts as low as 10<sup>2</sup> bacteria/mL are clinically significant [27]. Others have argued that reducing the threshold will ensure physicians do not miss true cases of UTI&rsquo;s and that patients will be treated properly [28,29]. The caveat is that patients must be symptomatic of a UTI as it is demonstrated that the bladder contains a microbiome [7,8,27,29].</p>\r\n<p>In addition to bacteria, we examined the fungus and virus detection in symptomatic patient urine samples. There were several cases for which yeast and viral particles were identified in combination with bacteria. Likewise, there were cases for which the fungus and virus were found independent of bacteria. These microbes may also be pathogenic. While this paper focuses on comparing bacteria in culture versus M-PCR, other articles will focus on fungal and virus detection.</p>\r\n<p>Strengths of this study include the large number of  symptomatic patients recruited from 37 different urology clinics in seven states, all serving in a community setting. The patients participating in the study likely reflect the larger population of UTI patients, to evaluate the performance of the increased sensitivity and specificity of PCR. These results should be relevant to clinicians attending to patients who present with UTI symptoms. The results of this prospective study of over 2500 patients with symptoms of urinary tract infection demonstrated the greater ability of M-PCR to detect bacteria over SUC, especially for detection of Grampositive  organisms and polymicrobial infections. Use of M-PCR  for evaluation of urinary tract infections might improve detection of pathogenic bacteria and consideration of antibiotic resistance and susceptibility. These advantages, in turn, might lead to more effective treatment [30-33].</p>\r\n<h3 class=\"subtitle target=\">Acknowledgment</h3>\r\n<p>All funding was provided by Pathnostics and Thermo Fisher</p>\r\n<h3 class=\"subsubtitle target=\">Conflicts of Interest</h3>\r\n<p>David Baunoch and Natalie Luke are employees of Pathnostics, Inc. Megan Campbell was an employee of Pathnostics, Inc at the time of the study.</p>', '<ul class=\"ullist target=\">\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC6502976/\">Medina M, Castillo-Pino E (2019) an introduction to the epidemiology and burden of urinary tract infections. Ther Adv Urol 11: 1756287219832172.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/31030820/\">Kolman KB (2019) Cystitis and Pyelonephritis: Diagnosis, Treatment, and Prevention. Prim Care 46: 191-202.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/11733475/\">Lawrenson RA, Logie JW (2001) Antibiotic failure in the treatment of urinary tract infections in young women. J Antimicrob Chemother 48: 895-901.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC1884592/\">Goettsch WG, Janknegt R, Herings RMC (2004) Increased treatment failure after 3-days&rsquo; courses of nitrofurantoin and trimethoprim for urinary tract infections in women: a population-based retrospective cohort study using the PHARMO database. Br J Clin Pharmacol 58:184-189.</a></li>\r\n    <li><a href=\"https://www.researchgate.net/publication/227542255_UTIs_the_challenge_of_treatment_failure_and_recurrent_infections\">Mayer R (2011) UTIs: the challenge of treatment failure and recurrent infections. Prescriber 22: 40-45.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/books/NBK436013/\">Sabih A, Leslie SW (2020) Complicated Urinary Tract Infections. In: StatPearls. Treasure Island (FL): StatPearls Publishing.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC4844725/\">Price TK, Dune T, Hilt EE, Thomas-White KJ, Kliethermes S, Br, et al. (2016) The Clinical Urine Culture: Enhanced Techniques Improve Detection of Clinically Relevant Microorganisms. J Clin Microbiol 54: 1216-1222.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/24371246/\">Hilt EE, McKinley K, Pearce MM, Rosenfeld AB, Zilliox MJ, et al. (2014) Urine is not sterile: use of enhanced urine culture techniques to detect resident bacterial flora in the adult female bladder. J Clin Microbiol 52: 871-876. </a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/19874298/\">Lehmann LE, Hauser S, Malinka T, Klaschik S, St&uuml;ber F, et al. (2010) Real-time polymerase chain-reaction detection of pathogens is feasible to supplement the diagnostic sequence for urinary tract infections. BJU Int 106: 114-120.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC4783162/\">Van der Zee A, Roorda L, Bosman G, Ossewaarde JM (2016) Molecular Diagnosis of Urinary Tract Infections by Semi-Quantitative Detection of Uropathogens in a Routine Clinical Hospital Setting. PLoS One 11: e0150755.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/31715272/\">Wojno KJ, Baunoch D, Luke N, Michael Opel, Howard Korman, et al. (2020) Multiplex PCR based urinary tract infection (UTI) analysis compared to traditional urine culture in identifying significant pathogens in symptomatic patients. Urology 136: 119-126.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3697662/\">Khasriya RS, Sathiananthamoorthy S, Ismail S, Kelsey M, Wilson M, et al. (2013) Spectrum of bacterial colonization associated with urothelial cells from patients with chronic lower urinary tract symptoms. J Clin Microbiol 51: 2054-2062.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/27227294/\">Kline KA, Lewis AL (2016) Gram-Positive Uropathogens, Polymicrobial Urinary Tract Infection, and the Emerging Microbiota of the Urinary Tract. Microbiol Spectr 4.</a></li>\r\n    <li><a href=\"https://www.researchgate.net/publication/262538867_Bacteraemia_caused_by_Actinobaculum_schaalii_An_overlooked_pathogen\">Sandlund J, Glim&aring;ker M, Svahn A, Brauner A (2014) Bacteraemia caused by Actinobaculum schaalii: An overlooked pathogen?. Scand J Infect Dis 46: 605-608.</a></li>\r\n    <li><a href=\"https://bjui-journals.onlinelibrary.wiley.com/doi/full/10.1111/j.1464-410X.2012.11739.x\">Olsen AB, Andersen PK, Bank S, S&oslash;by KM, Lund L et al. (2013) Actinobaculum schaalii, a commensal of the urogenital area. BJU International 112: 394-397.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/26551841/\">Lotte L, Lotte R, Durand M, Degand N, Ambrosetti D, et al. (2016) Infections related to Actinotignum schaalii (formerly Actinobaculum schaalii): a 3-year prospective observational study on 50 cases. Clinical Microbiology and Infection 22: 388-390.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/26577137/\">Lotte R, Lotte L, Ruimy R (2016) Actinotignum schaalii (formerly Actinobaculum schaalii): a newly recognized pathogen&mdash;review of the literature. Clinical Microbiology and Infection 22: 28-36.</a></li>\r\n    <li><a href=\"https://www.sciencedirect.com/science/article/abs/pii/S1341321X16000039\">Ogawa Y, Koizumi A, Kasahara K, Lee S-T, Yamada Y, et al. (2016) Bacteremia secondary to Alloscardovia omnicolens urinary tract infection. J Infect Chemother 22: 424-425.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3833147/\">Aygun G, Midilli K, Cilingir H, Yilmaz M, Kutukcu A et al. (2013) A fatal case of urosepsis due to Corynebacterium riegelii. Braz J Microbiol 44: 475-476.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC104597/\">Funke G, Lawson PA, Collins MD (1998) Corynebacterium riegelii sp. nov., an Unusual Species Isolated from Female Patients with Urinary Tract Infections. J Clin Microbiol 36: 624-627.</a></li>\r\n    <li><a href=\"https://www.tandfonline.com/doi/abs/10.1080/01443610802097690\">Latthe PM, Toozs-Hobson P, Gray J (2008) Mycoplasma and ureaplasma colonisation in women with lower urinary tract symptoms. J Obstet Gynaecol 28: 519&ndash;521.</a></li>\r\n    <li><a href=\"https://www.researchgate.net/publication/51726193_Accuracy_of_urethral_swab_and_urine_analysis_for_the_detection_of_Mycoplasma_hominis_and_Ureaplasma_urealyticum_in_women_with_lower_urinary_tract_symptoms\">Humburg J, Frei R, Wight E, Troeger C (2012) Accuracy of urethral swab and urine analysis for the detection of Mycoplasma hominis and Ureaplasma urealyticum in women with lower urinary tract symptoms. Arch Gynecol Obstet 285: 1049-1053.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3255964/\">Peters BM, Jabra-Rizk MA, O&rsquo;May GA, Costerton JW, Shirtliff ME (2012) Polymicrobial Interactions: Impact on Pathogenesis and Human Disease. Clinical Microbiology Reviews 25: 193-213.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3034215/\">Rogers GB, Hoffman LR, Whiteley M, Daniels TWV, Carroll MP, et al. (2010) Revealing the dynamics of polymicrobial infections: implications for antibiotic therapy. Trends Microbiol 18: 357-364.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC3534546/\">Schmiemann G, G&aacute;gyor I, Hummers-Pradier E, Bleidorn J (2012) Resistance profiles of urinary tract infections in general practice - an observational study. BMC Urol 12: 33.</a></li>\r\n    <li><a href=\"https://academic.oup.com/jac/article/58/5/1000/742331\">McNulty CAM, Richards J, Livermore DM, Little P, Charlett A, et al. (2006) Clinical relevance of laboratory-reported antibiotic resistance in acute uncomplicated urinary tract infection in primary care. J Antimicrob Chemother 58: 1000&ndash;1008.</a></li>\r\n    <li><a href=\"https://europepmc.org/article/med/6386456\">Stamm WE (1984) Quantitative urine cultures revisited European Journal of Clinical Microbiology. Microbiology 3: 279-281.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/31222664/\">Tullus K (2019) Defining urinary tract infection by bacterial colony counts: a case for less than 100,000 colonies/mL as the threshold. Pediatr Nephrol 34: 1651-1653.</a></li>\r\n    <li><a href=\"https://link.springer.com/article/10.1007/s00192-017-3528-8?shared-article-renderer\">Price T, Hilt E, Dune T, Mueller E, Wolfe A, et al. (2017) Urine trouble: should we think differently about UTI? International Urogynecology Journal 29: 205-210.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/22080266/\">Manhart LE, Broad JM, Golden MR (2011) Mycoplasma genitalium: Should We Treat and How? Clin Infect Dis 53: S129-S142.</a></li>\r\n    <li><a href=\"https://www.bmj.com/content/367/bmj.l5820\">Pinto-Sander N, Soni S (2019) Mycoplasma genitalium infection. BMJ 367: l5820.</a></li>\r\n    <li><a href=\"https://www.ncbi.nlm.nih.gov/pmc/articles/PMC6102385/\">Khaertynov KS, Anokhin VA, Rizvanov AA, Davidyuk YN, Semyenova DR, et al. (2018) Virulence Factors and Antibiotic Resistance of Klebsiella pneumoniae Strains Isolated From Neonates With Sepsis. Front Med (Lausanne) 5: 225.</a></li>\r\n    <li><a href=\"https://pubmed.ncbi.nlm.nih.gov/20952417/\">Cattoir V, Varca A, Greub G, Prod&rsquo;hom G, Legrand P, et al. (2010) In vitro susceptibility of Actinobaculum schaalii to 12 antimicrobial agents and molecular analysis of fluoroquinolone resistance. J Antimicrob Chemother 65: 2514-2517.</a></li>\r\n</ul>', '<p style=\"text-align: center;\">&nbsp;<input type=\"image\" src=\"/userfiles/1(19).png\" width=\"498\" height=\"409\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><b style=\"text-align: justify;\"><span lang=\"EN-IN\">Figure 1:</span></b><span lang=\"EN-IN\" style=\"text-align: justify;\"> Number of patients with UTI symptoms tested positive for various bacteria by SUC or M-PCR, listed in descending order according to SUC results, Gram-negative organisms grouped on the left and Gram-positive organisms grouped on the right. Among the 24 bacteria detected by M-PCR, SUC failed to detect 6 of them (marked with <span style=\"color: red;\">*</span>), including five Gram-positive bacteria. There were 3 bacterial species (marked with <span style=\"color: red;\">~</span>), detected by SUC that were not detected by the M-PCR because the panel did not include primers and probes for those three bacteria. Abbreviations: CoNS, Coagulase Negative <i>Staphylococci</i>; VGS, Viridans Group <i>Streptococci</i>.</span></span></span></p>\r\n<p class=\"MsoNormalCxSpMiddle\" style=\"margin-bottom:0in;margin-bottom:.0001pt;&#10;mso-add-space:auto;text-align:justify;line-height:normal\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><span lang=\"EN-IN\" style=\"color: red;\">*</span><span lang=\"EN-IN\">: Bacteria or bacterial species only detected by M-PCR.</span></span></span><span lang=\"EN-IN\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>\r\n<p style=\"text-align: center;\"><input type=\"image\" src=\"/userfiles/2(16).png\" width=\"232\" height=\"439\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><b style=\"text-align: justify;\"><span lang=\"EN-IN\">Figure 2:</span></b><span lang=\"EN-IN\" style=\"text-align: justify;\"> Bacteria detected in polymicrobial infections by SUC and M-PCR. Abbreviations<b>:</b> Mono: Monomicrobial; Poly: Polymicrobial; CoNS: Coagulase Negative <i>Staphylococci</i>; VGS: Viridans Group <i>Streptococci</i>.</span><br type=\"_moz\" />\r\n</span></span></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><span lang=\"EN-IN\" style=\"color: red;\">*</span><span lang=\"EN-IN\">: Bacteria or bacterial species only detected by M-PCR.</span></span></span></p>\r\n<p class=\"MsoNormalCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt;&#10;mso-add-space:auto;text-align:justify;line-height:normal\"><span lang=\"EN-IN\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', '<p><span class=\"citation target=\"><b>Citation: </b></span>Vollstedt A, Baunoch D, Wojno KJ, Luke N, Cline K, et al. (2020)  Multisite Prospective Comparison of Multiplex Polymerase Chain Reaction Testing with Urine Culture for Diagnosis  of Urinary Tract Infections in Symptomatic Patients. J Sur urology: JSU-102. DOI: 10.29011/ JSU-102.100002</p>', '<p style=\"text-align: center;\"><input type=\"image\" src=\"/userfiles/1(20).png\" width=\"470\" height=\"504\" /></p>\r\n<p style=\"text-align: center;\"><input type=\"image\" src=\"/userfiles/2(17).png\" width=\"447\" height=\"186\" /></p>\r\n<p style=\"text-align: center;\"><input type=\"image\" src=\"/userfiles/3(12).png\" width=\"676\" height=\"217\" /></p>\r\n<p style=\"text-align: center;\"><input type=\"image\" src=\"/userfiles/Supp 1.png\" width=\"346\" height=\"489\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><b style=\"text-align: justify;\"><span lang=\"EN-IN\">Supplementary Table 1:</span></b><span lang=\"EN-IN\" style=\"text-align: justify;\"> Bacteria detected by SUC or M-PCR in male and female patients with UTI symptoms. <i>P. agglomerans</i> was not listed in the table since it was not detected among the 2511 patients. Abbreviations: CoNS, Coagulase Negative <i>Staphylococci</i>; VGS, Viridans Group <i>Streptococci</i>.</span></span></span></p>\r\n<p style=\"text-align: center;\"><input type=\"image\" src=\"/userfiles/Supp 2(2).png\" width=\"381\" height=\"457\" /></p>\r\n<p style=\"text-align: justify;\"><span style=\"font-size: small;\"><span style=\"font-family: Arial;\"><b style=\"text-align: justify;\"><span lang=\"EN-IN\">Supplementary Table 2:</span></b><span lang=\"EN-IN\" style=\"text-align: justify;\"> Monomicrobial and polymicrobial bacteria detected by SUC or M-PCR. <i>P. agglomerans</i> was not listed in the table since it was not detected among the 2511 patients. Abbreviations<b>:</b> CoNS: Coagulase Negative <i>Staphylococci</i>; VGS: Viridans Group <i>Streptococci</i>.</span></span></span></p>\r\n<p class=\"MsoNormalCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt;&#10;mso-add-space:auto;text-align:justify;line-height:normal\"><span lang=\"EN-IN\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<p class=\"MsoNormalCxSpFirst\" style=\"margin-bottom:0in;margin-bottom:.0001pt;&#10;mso-add-space:auto;text-align:justify;line-height:normal\"><span lang=\"EN-IN\" style=\"font-size:10.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n<div align=\"center\">&nbsp;</div>', 'JSU-10272780.pdf    ', 'SurgicalUrology93497.jpg    ', '2020-07-18 04:11:21', '0', NULL);
